import React, { Component } from "react";

import Node from "./../Node/Node";

import { astar } from "../../algorithms/astar";
import { dijkstra } from "../../algorithms/dijkstra";
import { greedybestfirst } from "../../algorithms/greedybestfirst";
import { bfs } from "../../algorithms/bfs";
import { dfs } from "../../algorithms/dfs";

// Constants
const START_NODE_ROW = 0;
const START_NODE_COL = 0;
const END_NODE_ROW = 17;
const END_NODE_COL = 39;
const GRID_ROW_LENGTH = 18;
const GRID_COL_LENGTH = 40;

export default class Display extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      startNode: {},
      endNode: {},
      mouseIsPressed: false,
      mouseHoldsStart: false,
      mouseHoldsEnd: false,
      inProgress: false,
      simulationSpeed: 10,
    };
  }

  componentDidMount() {
    const grid = createGrid();
    this.setState({ grid });
    this.setState({ startNode: grid[START_NODE_ROW][START_NODE_COL] });
    this.setState({ endNode: grid[END_NODE_ROW][END_NODE_COL] });
  }

  handleMouseDown(row, col) {
    const { startNode, endNode } = this.state;

    // Check to see if the mouse clicked on the start
    // or end point
    if (row == startNode.row && col == startNode.col) {
      this.setState({
        mouseIsPressed: true,
        mouseHoldsStart: true,
      });
    } else if (row == endNode.row && col == endNode.col) {
      this.setState({
        mouseIsPressed: true,
        mouseHoldsEnd: true,
      });
    } else {
      const newGrid = getNewGridWithToggledObstacle(this.state.grid, row, col);
      this.setState({ grid: newGrid, mouseIsPressed: true });
    }
  }

  handleMouseUp(row, col) {
    // If the user releases the start on a grid
    // set the start on that grid
    if (this.state.mouseHoldsStart) {
      this.setState({ startNode: this.state.grid[row][col] });
    } else if (this.state.mouseHoldsEnd) {
      this.setState({ endNode: this.state.grid[row][col] });
    }

    this.setState({ mouseIsPressed: false });
    this.setState({ mouseHoldsStart: false });
    this.setState({ mouseHoldsEnd: false });
  }

  handleMouseOut(row, col) {
    // If the mouse isn't held down do not do anything here
    if (!this.state.mouseIsPressed) return;

    if (this.state.mouseHoldsStart) {
      const newGrid = getNewGridWithNewStart(this.state.grid, row, col);
      this.setState({ grid: newGrid });
    }
    if (this.state.mouseHoldsEnd) {
      const newGrid = getNewGridWithNewEnd(this.state.grid, row, col);
      this.setState({ grid: newGrid });
    }
  }

  handleMouseEnter(row, col) {
    // If the mouse isn't held down do not do anything here
    if (!this.state.mouseIsPressed) return;

    // If we enter a new grid with the start held, generate a new start
    if (this.state.mouseHoldsStart) {
      const newGrid = getNewGridWithNewStart(this.state.grid, row, col);
      this.setState({ grid: newGrid });
    }
    // If we enter a new grid with the end held, generate a new end
    else if (this.state.mouseHoldsEnd) {
      const newGrid = getNewGridWithNewEnd(this.state.grid, row, col);
      this.setState({ grid: newGrid });
    }
    // Toogle the obstacles
    else {
      const newGrid = getNewGridWithToggledObstacle(this.state.grid, row, col);
      this.setState({ grid: newGrid });
    }
  }

  animateNodeList(visitedNodesInOrder, shortestPathInOrder) {
    for (let i = 0; i <= visitedNodesInOrder.length; i++) {
      // If there is a solution, animate it
      // If not skip it and just animate the visited nodes
      if (i === visitedNodesInOrder.length) {
        setTimeout(() => {
          this.animateShortestPath(shortestPathInOrder);
        }, this.state.simulationSpeed * i);
        return;
      }
      setTimeout(() => {
        const node = visitedNodesInOrder[i];
        document.getElementById(`node-${node.row}-${node.col}`).className =
          "node node-visited";
      }, this.state.simulationSpeed * i);
    }
  }

  animateShortestPath(shortestPathInOrder) {
    for (let i = 0; i < shortestPathInOrder.length; i++) {
      setTimeout(() => {
        const node = shortestPathInOrder[i];
        document.getElementById(`node-${node.row}-${node.col}`).className =
          "node node-shortest-path";
      }, 40 * i);
    }
  }

  visualizeAstar(grid, startNode, endNode) {
    // Grab our values and call the animate function for visualization
    const visitedNodesInOrder = astar(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeDijkstra(grid, startNode, endNode) {
    // Grab our values and call the animate function for visualization
    const visitedNodesInOrder = dijkstra(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeGreedy(grid, startNode, endNode) {
    // Grab our values and call the animate function for visualization
    const visitedNodesInOrder = greedybestfirst(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeBFS(grid, startNode, endNode) {
    // Grab our values and call the animate function for visualization
    const visitedNodesInOrder = bfs(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeDFS(grid, startNode, endNode) {
    // Grab our values and call the animate function for visualization
    const visitedNodesInOrder = dfs(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  render() {
    const { grid, mouseIsPressed, startNode, endNode } = this.state;
    return (
      <>
        <button onClick={() => this.visualizeAstar(grid, startNode, endNode)}>
          A*
        </button>
        <button
          onClick={() => this.visualizeDijkstra(grid, startNode, endNode)}
        >
          Dijkstra
        </button>
        <button onClick={() => this.visualizeGreedy(grid, startNode, endNode)}>
          Greedy-best-first
        </button>
        <button onClick={() => this.visualizeBFS(grid, startNode, endNode)}>
          BFS
        </button>
        <button onClick={() => this.visualizeDFS(grid, startNode, endNode)}>
          DFS
        </button>
        <div className="grid">
          {grid.map((row, rowId) => {
            return (
              <div key={rowId}>
                {row.map((node, nodeId) => {
                  const { row, col, isStartNode, isEndNode, isObstacle } = node;
                  return (
                    <Node
                      key={nodeId}
                      row={row}
                      col={col}
                      isStartNode={isStartNode}
                      isEndNode={isEndNode}
                      isObstacle={isObstacle}
                      mouseIsPressed={mouseIsPressed}
                      onMouseOut={(row, col) => this.handleMouseOut(row, col)}
                      onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                      onMouseEnter={(row, col) =>
                        this.handleMouseEnter(row, col)
                      }
                      onMouseUp={(row, col) => this.handleMouseUp(row, col)}
                    ></Node>
                  );
                })}
              </div>
            );
          })}
        </div>
      </>
    );
  }
}

