import React, { Component } from "react";

export default class Description extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  getAlgorithmDescription(algorithm) {
    if (algorithm == "A*")
      return "A*, a balance of greedy and dijkstra. Uses both the start and end node to expand. Returns the shortest path";
    else if (algorithm == "Dijkstra")
      return "Dijkstra, stays as close as possible to the start node and expands outwards. Returns the shortest path.";
    else if (algorithm == "GBF")
      return "Greedy-Best-First, a speedy algorithm that uses the end node and tries to attack it. Will not always return the shortest path.";
    else if (algorithm == "BFS")
      return "Breadth-First-Search, doesn't use the start or end node, but rather expands level by level. Returns the shortest path.";
    else if (algorithm == "DFS")
      return "Depth-First-Search, a horrible pathfinding algorithm and has no sense of direction. Will not return the shortest path unless you are lucky.";
    else return "";
  }

  render() {
    const { activeAlgorithm } = this.props;

    const description = this.getAlgorithmDescription(activeAlgorithm);

    return <div className="descriptionContainer">{description}</div>;
  }
}
