import React, { Component } from "react";
import Navigationbar from "../NavigationBar/NavigationBar";
import Display from "../Display/Display";
import Tutorial from "../Tutorial/Tutorial";
import Description from "../DescriptionText/Description";

export default class Simulation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // Defaults
      activeAlgorithm: "A*",
      simulatorSpeed: 1.0,
      showTutorial: true,
    };
    this.onClickAlgorithm = this.onClickAlgorithm.bind(this);
    this.onClickSpeed = this.onClickSpeed.bind(this);
    this.createTutorialEvent = this.createTutorialEvent.bind(this);
  }

  onComponentDidMount() {
    this.setState({ tutorialEventCalled: true });
  }

  createTutorialEvent() {
    this.setState({ showTutorial: true });
  }

  closeTutorial() {
    this.setState({ showTutorial: false });
  }

  // Item clicks for algorithms
  onClickAlgorithm(e, algorithm) {
    e.preventDefault();
    if (algorithm == "A*") this.setState({ activeAlgorithm: "A*" });
    else if (algorithm == "Dijkstra")
      this.setState({ activeAlgorithm: "Dijkstra" });
    else if (algorithm == "GBF") this.setState({ activeAlgorithm: "GBF" });
    else if (algorithm == "BFS") this.setState({ activeAlgorithm: "BFS" });
    else if (algorithm == "DFS") this.setState({ activeAlgorithm: "DFS" });
  }

  // Item Click for Simulation Speed
  onClickSpeed(e, speed) {
    e.preventDefault();
    this.setState({ simulatorSpeed: speed });
  }

  render() {
    const { tutorialEventCalled } = this.state;

    return (
      <div>
        <Tutorial></Tutorial>
        <Navigationbar
          activeAlgorithm={this.state.activeAlgorithm}
          simulatorSpeed={this.state.simulatorSpeed}
          onClickAlgorithm={this.onClickAlgorithm}
          onClickSpeed={this.onClickSpeed}
          createTutorialEvent={this.createTutorialEvent}
        ></Navigationbar>
        <Description activeAlgorithm={this.state.activeAlgorithm}></Description>
        <Display
          activeAlgorithm={this.state.activeAlgorithm}
          simulatorSpeed={this.state.simulatorSpeed}
        ></Display>
      </div>
    );
  }
}
