import React, { Component } from "react";

import {
  Navbar,
  Nav,
  NavDropdown,
  Form,
  FormControl,
  Button,
} from "react-bootstrap";

export default class NavigationBar extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {}

  // Drop Down Menu Toggles
  showAlgorithmDropdown(e) {
    e.preventDefault();
    this.setState((algoDropDown) => ({
      isAlgoToggleOn: !algoDropDown.isAlgoToggleOn,
      isSpeedToggleOn: false,
      isAboutAlgoToggleOn: false,
      isAboutStuctToggleOn: false,
    }));
  }

  showSpeedDropdown(e) {
    e.preventDefault();
    this.setState((speedDropDown) => ({
      isAlgoToggleOn: false,
      isSpeedToggleOn: !speedDropDown.isSpeedToggleOn,
      isAboutAlgoToggleOn: false,
      isAboutStuctToggleOn: false,
    }));
  }

  showAboutAlgorithmsDropdown(e) {
    e.preventDefault();
    this.setState((aboutAlgoDropDown) => ({
      isAlgoToggleOn: false,
      isSpeedToggleOn: false,
      isAboutAlgoToggleOn: !aboutAlgoDropDown.isAboutAlgoToggleOn,
      isAboutStuctToggleOn: false,
    }));
  }

  showAboutStructureDropdown(e) {
    e.preventDefault();
    this.setState((aboutStructDropDown) => ({
      isAlgoToggleOn: false,
      isSpeedToggleOn: false,
      isAboutAlgoToggleOn: false,
      isAboutStuctToggleOn: !aboutStructDropDown.isAboutStructToggleOn,
    }));
  }

  onClick(e) {
    e.preventDefault();
    this.setState((algoDropDown) => ({
      isAstarActive: true,
    }));
  }

  render() {
    const classAlgoDropdownMenu =
      "dropdown-menu" + (this.state.isAlgoToggleOn ? " show" : "");
    const classActiveItem1 =
      "dropdown-item" + (this.state.isAstarActive ? " active" : "");

    const classSpeedDropdownMenu =
      "dropdown-menu" + (this.state.isSpeedToggleOn ? " show" : "");

    const classAboutAlgoDropdownMenu =
      "dropdown-menu" + (this.state.isAboutAlgoToggleOn ? " show" : "");

    const classAboutStructureDropdownMenu =
      "dropdown-menu" + (this.state.isAboutStructToggleOn ? " show" : "");

    return (
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="/">
          Path Finder Simulator
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item dropdown">
              <a
                class="nav-link dropdown-toggle"
                href="#"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showAlgorithmDropdown(e);
                }}
              >
                Choose Algorithm
              </a>
              <div
                className={classAlgoDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a
                  className={classActiveItem1}
                  onClick={(e) => {
                    this.onClick(e);
                  }}
                >
                  A*
                </a>
                <a className="dropdown-item">Dijkstra</a>
                <a className="dropdown-item">Greedy-Best-First</a>
                <a className="dropdown-item">Breadth-First-Search</a>
                <a className="dropdown-item">Depth-First-Search</a>
              </div>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="/"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showSpeedDropdown(e);
                }}
              >
                Adjust Simulation Speed
              </a>
              <div
                className={classSpeedDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a className="dropdown-item">25%</a>
                <a className="dropdown-item">50%</a>
                <a className="dropdown-item">75%</a>
                <a className="dropdown-item">100%</a>
              </div>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="/"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showAboutAlgorithmsDropdown(e);
                }}
              >
                About Pathfinding Algorithms
              </a>
              <div
                className={classAboutAlgoDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a className="dropdown-item">A*</a>
                <a className="dropdown-item">Dijkstra</a>
                <a className="dropdown-item">Greedy-First-Search</a>
                <a className="dropdown-item">Breadth-First-Search</a>
                <a className="dropdown-item">Depth-First-Search</a>
              </div>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="/"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showAboutStructuresDropdown(e);
                }}
              >
                About Pathfinding Data Structures
              </a>
              <div
                className={classAboutStructureDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a className="dropdown-item">Stack</a>
                <a className="dropdown-item">Queue</a>
                <a className="dropdown-item">Priority Queue</a>
              </div>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="/">
                Instructions <span className="sr-only">(current)</span>
              </a>
            </li>
          </ul>
        </div>
      </nav>
    );
  }
}
