import React, { Component } from "react";

import Node from "./../Node/Node";
import { astar } from "../../algorithms/astar";

// Constants
const START_NODE_ROW = 0;
const START_NODE_COL = 0;
const END_NODE_ROW = 19;
const END_NODE_COL = 19;
const GRID_ROW_LENGTH = 20;
const GRID_COL_LENGTH = 20;

export default class Display extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
    };
  }

  componentDidMount() {
    const grid = createGrid();
    addNeighborNodes(grid);
    this.setState({ grid });
  }

  animateNodeList(visitedNodesInOrder) {
    for (let i = 0; i < visitedNodesInOrder.length; i++) {
      setTimeout(() => {
        const node = visitedNodesInOrder[i];
        const newGrid = this.state.grid.slice();
        const newNode = {
          ...node,
          isVisited: true,
        };
        newGrid[node.row][node.col] = newNode;
        this.setState({ grid: newGrid });
      }, 1000 * i);
    }
  }

  visualizeAstar() {
    // Grab our values and call the animate function for visualization
    const { grid } = this.state;
    const startNode = grid[START_NODE_ROW][START_NODE_COL];
    const endNode = grid[END_NODE_ROW][END_NODE_COL];
    const visitedNodesInOrder = astar(startNode, endNode);
    this.animateNodeList(visitedNodesInOrder);
  }

  render() {
    const { grid } = this.state;
    return (
      <div>
        <button onClick={() => this.visualizeAstar()} Run astar></button>
        <div className="grid">
          {grid.map((row, rowIdx) => {
            return (
              <div key={rowIdx}>
                {row.map((node, nodeIdx) => {
                  const { isStartNode, isEndNode, isVisited } = node;
                  return (
                    <Node
                      key={nodeIdx}
                      isStartNode={isStartNode}
                      isEndNode={isEndNode}
                      isVisited={isVisited}
                    ></Node>
                  );
                })}
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

const createGrid = () => {
  // Display Grid and Nodes
  const grid = [];
  for (let row = 0; row < GRID_ROW_LENGTH; row++) {
    const currentRow = [];
    for (let col = 0; col < GRID_COL_LENGTH; col++) {
      currentRow.push(createNewNode(row, col));
    }
    grid.push(currentRow);
  }

  return grid;
};

const createNewNode = (col, row) => {
  const newNode = {
    col,
    row,
    g: Infinity,
    h: Infinity,
    isStartNode: row === START_NODE_ROW && col === START_NODE_COL,
    isEndNode: row === END_NODE_ROW && col === END_NODE_COL,
    isVisited: false,
    isObstacle: false,
    neighborNodes: [],
    parentNode: null,
  };

  return newNode;
};

const addNeighborNodes = (grid) => {
  // Adds each neighbor node in the grid.
  // Have to make sure we do not run out of bounds

  for (let i = 0; i < GRID_ROW_LENGTH; i++) {
    for (let j = 0; j < GRID_COL_LENGTH; j++) {
      if (i < GRID_COL_LENGTH - 1) {
        grid[i][j].neighborNodes.push(grid[i + 1][j]);
      }
      if (i > 0) {
        grid[i][j].neighborNodes.push(grid[i - 1][j]);
      }
      if (j < GRID_ROW_LENGTH - 1) {
        grid[i][j].neighborNodes.push(grid[i][j + 1]);
      }
      if (j > 0) {
        grid[i][j].neighborNodes.push(grid[i][j - 1]);
      }
      if (i > 0 && j > 0) {
        grid[i][j].neighborNodes.push(grid[i - 1][j - 1]);
      }
      if (i < GRID_COL_LENGTH - 1 && j > 0) {
        grid[i][j].neighborNodes.push(grid[i + 1][j - 1]);
      }
      if (i > 0 && j < GRID_ROW_LENGTH - 1) {
        grid[i][j].neighborNodes.push(grid[i - 1][j + 1]);
      }
      if (i < GRID_COL_LENGTH - 1 && j < GRID_ROW_LENGTH - 1) {
        grid[i][j].neighborNodes.push(grid[i + 1][j + 1]);
      }
    }
  }
};
