import React, { Component } from "react";

import "./Node.css";

export default class Node extends Component {
  constructor(props) {
    super(props);
    this.state = {
      row,
      col,
    };
  }

  render() {
    const {
      row,
      col,
      isStartNode,
      isEndNode,
      isVisited,
      isObstacle,
      handleMouseDown,
      handleMouseEnter,
      handleMouseUp,
    } = this.props;

    const getNodeClass = isStartNode
      ? "node-start"
      : isEndNode
      ? "node-end"
      : isVisited
      ? "node-visited"
      : isObstacle
      ? "node-obstacle"
      : "";

    return (
      <div
        className={`node ${getNodeClass}`}
        onMouseDown={() => handleMouseDown(row, col)}
        onMouseEnter={() => handleMouseEnter(row, col)}
        onMouseUp={() => handleMouseUp()}
      ></div>
    );
  }
}
