import React, { Component } from "react";

import "./Tutorial.css";

export default class AboutObstacles extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <div className="tutorial">
        <h3 className="title">Placing Obtacles</h3>
        <h6 className="bold-paragraph">
          Toggle any obstacle by simply clicking on an empty grid cell. Clicking
          on the obstacle again will remove it.
        </h6>
        <img
          className="small-image"
          src={require("./../../gifs/toggleobstacle.gif")}
        ></img>
        <h6 className="bold-paragraph">
          Press and drag to continously toggle obstacles. Dragging over
          obstacles will continously remove them.
        </h6>
        <img
          className="small-image"
          src={require("./../../gifs/dragobstacle.gif")}
        ></img>
        <div className="page">3/9</div>
        <button
          className="next"
          onClick={() => {
            this.props.incrementPage();
          }}
        >
          Next
        </button>
        <button
          className="previous"
          onClick={() => {
            this.props.decrementPage();
          }}
        >
          Previous
        </button>
        <button
          className="skip"
          onClick={() => {
            this.props.skip();
          }}
        >
          Skip
        </button>
      </div>
    );
  }
}
