import React, { Component } from "react";

import Node from "./../Node/Node";

import { astar } from "../../algorithms/astar";
import { dijkstra } from "../../algorithms/dijkstra";
import { greedybestfirst } from "../../algorithms/greedybestfirst";
import { bfs } from "../../algorithms/bfs";
import { dfs } from "../../algorithms/dfs";

// Constants
const START_NODE_ROW = 0;
const START_NODE_COL = 0;
const END_NODE_ROW = 19;
const END_NODE_COL = 39;
const GRID_ROW_LENGTH = 23;
const GRID_COL_LENGTH = 40;
const ANIMATION_SPEED = 30;

export default class Display extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      mouseIsPressed: false,
    };
  }

  componentDidMount() {
    const grid = createGrid();
    this.setState({ grid });
  }

  handleMouseDown(row, col) {
    const newGrid = getNewGridWithToggledObstacle(this.state.grid, row, col);
    this.setState({ grid: newGrid, mouseIsPressed: true });
  }

  handleMouseUp() {
    this.setState({ mouseIsPressed: false });
  }

  handleMouseEnter(row, col) {
    if (!this.state.mouseIsPressed) return;
    const newGrid = getNewGridWithToggledObstacle(this.state.grid, row, col);
    this.setState({ grid: newGrid });
  }

  animateNodeList(visitedNodesInOrder, shortestPathInOrder) {
    for (let i = 0; i <= visitedNodesInOrder.length; i++) {
      if (i === visitedNodesInOrder.length) {
        setTimeout(() => {
          this.animateShortestPath(shortestPathInOrder);
        }, ANIMATION_SPEED * i);
        return;
      }
      setTimeout(() => {
        const node = visitedNodesInOrder[i];
        document.getElementById(`node-${node.row}-${node.col}`).className =
          "node node-visited";
      }, ANIMATION_SPEED * i);
    }
  }

  animateShortestPath(shortestPathInOrder) {
    for (let i = 0; i < shortestPathInOrder.length; i++) {
      setTimeout(() => {
        const node = shortestPathInOrder[i];
        document.getElementById(`node-${node.row}-${node.col}`).className =
          "node node-shortest-path";
      }, 40 * i);
    }
  }

  resetPath(grid) {
    const gridWithoutPath = reconstructGrid(grid);

    for (let i = 0; i < GRID_ROW_LENGTH; i++) {
      for (let j = 0; j < GRID_COL_LENGTH; j++) {
        const node = grid[i][j];
        document.getElementById(`node-${node.row}-${node.col}`).className =
          "node node";
      }
    }

    this.setState({ grid: gridWithoutPath });
  }

  visualizeAstar(grid) {
    // Grab our values and call the animate function for visualization
    const startNode = grid[START_NODE_ROW][START_NODE_COL];
    const endNode = grid[END_NODE_ROW][END_NODE_COL];
    const visitedNodesInOrder = astar(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeDijkstra(grid) {
    // Grab our values and call the animate function for visualization
    const startNode = grid[START_NODE_ROW][START_NODE_COL];
    const endNode = grid[END_NODE_ROW][END_NODE_COL];
    const visitedNodesInOrder = dijkstra(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeGreedy(grid) {
    // Grab our values and call the animate function for visualization
    const startNode = grid[START_NODE_ROW][START_NODE_COL];
    const endNode = grid[END_NODE_ROW][END_NODE_COL];
    const visitedNodesInOrder = greedybestfirst(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeBFS(grid) {
    // Grab our values and call the animate function for visualization
    const startNode = grid[START_NODE_ROW][START_NODE_COL];
    const endNode = grid[END_NODE_ROW][END_NODE_COL];
    const visitedNodesInOrder = bfs(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  visualizeDFS(grid) {
    // Grab our values and call the animate function for visualization
    const startNode = grid[START_NODE_ROW][START_NODE_COL];
    const endNode = grid[END_NODE_ROW][END_NODE_COL];
    const visitedNodesInOrder = dfs(grid, startNode, endNode);
    const shortestPathInOrder = getResultPath(endNode);
    this.animateNodeList(visitedNodesInOrder, shortestPathInOrder);
  }

  render() {
    const { grid, mouseIsPressed } = this.state;
    return (
      <>
        <button onClick={() => this.visualizeAstar(grid)}>A*</button>
        <button onClick={() => this.visualizeDijkstra(grid)}>Dijkstra</button>
        <button onClick={() => this.visualizeGreedy(grid)}>
          Greedy-best-first
        </button>
        <button onClick={() => this.visualizeBFS(grid)}>BFS</button>
        <button onClick={() => this.visualizeDFS(grid)}>DFS</button>
        <button onClick={() => this.resetPath(grid)}>Reset Path</button>
        <div className="grid">
          {grid.map((row, rowId) => {
            return (
              <div key={rowId}>
                {row.map((node, nodeId) => {
                  const { row, col, isStartNode, isEndNode, isObstacle } = node;
                  return (
                    <Node
                      key={nodeId}
                      row={row}
                      col={col}
                      isStartNode={isStartNode}
                      isEndNode={isEndNode}
                      isObstacle={isObstacle}
                      mouseIsPressed={mouseIsPressed}
                      onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                      onMouseEnter={(row, col) =>
                        this.handleMouseEnter(row, col)
                      }
                      onMouseUp={() => this.handleMouseUp()}
                    ></Node>
                  );
                })}
              </div>
            );
          })}
        </div>
      </>
    );
  }
}

const createGrid = () => {
  // Create a new grid which holds nodes
  const grid = [];
  for (let row = 0; row < GRID_ROW_LENGTH; row++) {
    const currentRow = [];
    for (let col = 0; col < GRID_COL_LENGTH; col++) {
      currentRow.push(createNewNode(row, col));
    }
    grid.push(currentRow);
  }

  return grid;
};

const reconstructGrid = (savedGrid) => {
  // Create a new grid which holds nodes

  for (let row = 0; row < GRID_ROW_LENGTH; row++) {
    const currentRow = [];
    for (let col = 0; col < GRID_COL_LENGTH; col++) {
      if (savedGrid[row][col].isStartNode) {
        let newNode = createNewNode(row, col);
        newNode.startNode = true;
        currentRow.push(newNode);
      } else if (savedGrid[row][col].isEndNode) {
        let newNode = createNewNode(row, col);
        newNode.isEndNode = true;
        currentRow.push(newNode);
      } else if (savedGrid[row][col].isObstacle) {
        let newNode = createNewNode(row, col);
        newNode.isObstacle = true;
        currentRow.push(newNode);
      } else {
        let newNode = createNewNode(row, col);
        currentRow.push(newNode);
      }
    }
    savedGrid.push(currentRow);
  }

  return savedGrid;
};

const createNewNode = (row, col) => {
  // Create a new node
  const newNode = {
    row,
    col,
    g: Infinity,
    h: Infinity,
    isStartNode: row === START_NODE_ROW && col === START_NODE_COL,
    isEndNode: row === END_NODE_ROW && col === END_NODE_COL,
    isVisited: false,
    isObstacle: false,
    neighborNodes: [],
    parentNode: null,
  };

  return newNode;
};

const getNewGridWithToggledObstacle = (grid, row, col) => {
  const newGrid = grid.slice();
  const node = newGrid[row][col];
  const newNode = {
    ...node,
    isObstacle: !node.isObstacle,
  };
  newGrid[row][col] = newNode;
  return newGrid;
};

const getResultPath = (endNode) => {
  const shortestPathInOrder = [];
  let scanningNode = endNode.parentNode;

  while (scanningNode.parentNode) {
    shortestPathInOrder.push(scanningNode);
    scanningNode = scanningNode.parentNode;
  }

  return shortestPathInOrder;
};
