import React, { Component } from "react";

import {
  Navbar,
  Nav,
  NavDropdown,
  Form,
  FormControl,
  Button,
} from "react-bootstrap";

export default class NavigationBar extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {}

  // Drop Down Menu Toggles
  showDropdown(e, dropdown) {
    e.preventDefault();
    if (dropdown == "algorithm") this.setState({ activeDropDown: "algorithm" });
    else if (dropdown == "speed") this.setState({ activeDropDown: "speed" });
    else if (dropdown == "aboutAlgo")
      this.setState({ activeDropDown: "aboutAlgo" });
    else if (dropdown == "aboutStruct")
      this.setState({ activeDropDown: "aboutStruct" });
  }

  // Item clicks for algorithms
  onClickAlgorithm(e, algorithm) {
    e.preventDefault();
    if (algorithm == "astar") this.setState({ activeAlgorithm: "astar" });
    else if (algorithm == "dijkstra")
      this.setState({ activeAlgorithm: "dijkstra" });
    else if (algorithm == "gbf") this.setState({ activeAlgorithm: "gbf" });
    else if (algorithm == "bfs") this.setState({ activeAlgorithm: "bfs" });
    else if (algorithm == "dfs") this.setState({ activeAlgorithm: "dfs" });
  }

  // Item Click for Simulation Speed

  render() {
    // Algorithm Drop Down
    const classAlgoDropdownMenu =
      "dropdown-menu" +
      (this.state.activeDropDown == "algorithm" ? " show" : "");
    const classAstarActive =
      "dropdown-item" +
      (this.state.activeAlgorithm == "astar" ? " active" : "");
    const classDijkstraActive =
      "dropdown-item" +
      (this.state.activeAlgorithm == "dijkstra" ? " active" : "");
    const classGBFActive =
      "dropdown-item" + (this.state.activeAlgorithm == "gbf" ? " active" : "");
    const classBFSActive =
      "dropdown-item" + (this.state.activeAlgorithm == "bfs" ? " active" : "");
    const classDFSActive =
      "dropdown-item" + (this.state.activeAlgorithm == "dfs" ? " active" : "");

    // Speed Adjustment Dropdown
    const classSpeedDropdownMenu =
      "dropdown-menu" + (this.state.activeDropDown == "speed" ? " show" : "");

    // About Algorithms Dropdown
    const classAboutAlgoDropdownMenu =
      "dropdown-menu" +
      (this.state.activeDropDown == "aboutAlgo" ? " show" : "");

    // About Daa Structures DropDown
    const classAboutStructDropdownMenu =
      "dropdown-menu" +
      (this.state.activeDropDown == "aboutStruct" ? " show" : "");

    return (
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="/">
          Path Finder Simulator
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item dropdown">
              <a
                class="nav-link dropdown-toggle"
                href="#"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showDropdown(e, "algorithm");
                }}
              >
                Choose Algorithm
              </a>
              <div
                className={classAlgoDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a
                  className={classAstarActive}
                  onClick={(e) => {
                    this.onClickAlgorithm(e, "astar");
                    this.setState({ activeDropDown: null });
                  }}
                >
                  A*
                </a>
                <a
                  className={classDijkstraActive}
                  onClick={(e) => {
                    this.onClickAlgorithm(e, "dijkstra");
                    this.setState({ activeDropDown: null });
                  }}
                >
                  Dijkstra
                </a>
                <a
                  className={classGBFActive}
                  onClick={(e) => {
                    this.onClickAlgorithm(e, "gbf");
                    this.setState({ activeDropDown: null });
                  }}
                >
                  Greedy-Best-First
                </a>
                <a
                  className={classBFSActive}
                  onClick={(e) => {
                    this.onClickAlgorithm(e, "bfs");
                    this.setState({ activeDropDown: null });
                  }}
                >
                  Breadth-First-Search
                </a>
                <a
                  className={classDFSActive}
                  onClick={(e) => {
                    this.onClickAlgorithm(e, "dfs");
                    this.setState({ activeDropDown: null });
                  }}
                >
                  Depth-First-Search
                </a>
              </div>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="/"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showDropdown(e, "speed");
                }}
              >
                Adjust Simulation Speed
              </a>
              <div
                className={classSpeedDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a className="dropdown-item">25%</a>
                <a className="dropdown-item">50%</a>
                <a className="dropdown-item">75%</a>
                <a className="dropdown-item">100%</a>
              </div>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="/"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showAboutAlgorithmsDropdown(e, "aboutAlgo");
                }}
              >
                About Pathfinding Algorithms
              </a>
              <div
                className={classAboutAlgoDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a className="dropdown-item">A*</a>
                <a className="dropdown-item">Dijkstra</a>
                <a className="dropdown-item">Greedy-First-Search</a>
                <a className="dropdown-item">Breadth-First-Search</a>
                <a className="dropdown-item">Depth-First-Search</a>
              </div>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="/"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
                onClick={(e) => {
                  this.showAboutStructuresDropdown(e, "aboutStructs");
                }}
              >
                About Pathfinding Data Structures
              </a>
              <div
                className={classAboutStructDropdownMenu}
                aria-labelledby="navbarDropdown"
              >
                <a className="dropdown-item">Stack</a>
                <a className="dropdown-item">Queue</a>
                <a className="dropdown-item">Priority Queue</a>
              </div>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="/">
                Instructions <span className="sr-only">(current)</span>
              </a>
            </li>
          </ul>
        </div>
      </nav>
    );
  }
}
