import React, { Component } from "react";

import Node from "./../Node/Node";
import { astar } from "../../algorithms/astar";

// Constants
const START_NODE_ROW = 0;
const START_NODE_COL = 0;
const END_NODE_ROW = 19;
const END_NODE_COL = 39;
const GRID_ROW_LENGTH = 20;
const GRID_COL_LENGTH = 40;

export default class Display extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      mouseIsPressed: false,
    };
  }

  componentDidMount() {
    const grid = createGrid();
    this.setState({ grid });
  }

  handleMouseDown(row, col) {
    const newGrid = getNewGridWithToggledObstacle(this.state.grid, row, col);
    this.setState({ grid: newGrid, mouseIsPressed: true });
  }

  handleMouseUp() {
    this.setState({ mouseIsPressed: false });
  }

  handleMouseEnter(row, col) {
    if (!this.state.mouseIsPressed) return;
    console.log("mouse is pressed");
    const newGrid = getNewGridWithToggledObstacle(this.state.grid, row, col);
    this.setState({ grid: newGrid });
  }

  animateNodeList(visitedNodesInOrder) {
    for (let i = 0; i < visitedNodesInOrder.length; i++) {
      setTimeout(() => {
        const node = visitedNodesInOrder[i];

        // React not refreshing correctly for animation
        // so have to do it this way for now
        document.getElementById(`node-${node.row}-${node.col}`).className =
          "node node-visited";
      }, 25 * i);
    }
  }

  visualizeAstar(grid) {
    // Grab our values and call the animate function for visualization
    const startNode = grid[START_NODE_ROW][START_NODE_COL];
    const endNode = grid[END_NODE_ROW][END_NODE_COL];
    const visitedNodesInOrder = astar(grid, startNode, endNode);
    this.animateNodeList(visitedNodesInOrder);
  }

  reset() {
    const { grid } = this.state;
    for (let i = 0; i < GRID_COL_LENGTH; i++) {
      for (let j = 0; j < GRID_ROW_LENGTH; j++) {
        if (grid[i][j].isVisited) {
          const node = grid[i][j];
          document.getElementById(`node-${node.row}-${node.col}`).className =
            "node node";
        }
      }
    }
    const newGrid = createGrid();
    this.setState({ newGrid });
  }

  render() {
    const { grid, mouseIsPressed } = this.state;
    return (
      <>
        <button onClick={() => this.visualizeAstar(grid)}>A*</button>
        <button onClick={() => this.reset()}>Reset</button>
        <div className="grid">
          {grid.map((row, rowId) => {
            return (
              <div key={rowId}>
                {row.map((node, nodeId) => {
                  const { row, col, isStartNode, isEndNode, isObstacle } = node;
                  return (
                    <Node
                      key={nodeId}
                      row={row}
                      col={col}
                      isStartNode={isStartNode}
                      isEndNode={isEndNode}
                      isObstacle={isObstacle}
                      mouseIsPressed={mouseIsPressed}
                      onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                      onMouseEnter={(row, col) =>
                        this.handleMouseEnter(row, col)
                      }
                      onMouseUp={() => this.handleMouseUp()}
                    ></Node>
                  );
                })}
              </div>
            );
          })}
        </div>
      </>
    );
  }
}

const createGrid = () => {
  // Create a new grid which holds nodes
  const grid = [];
  for (let row = 0; row < GRID_ROW_LENGTH; row++) {
    const currentRow = [];
    for (let col = 0; col < GRID_COL_LENGTH; col++) {
      currentRow.push(createNewNode(row, col));
    }
    grid.push(currentRow);
  }

  return grid;
};

const createNewNode = (row, col) => {
  // Create a new node
  const newNode = {
    row,
    col,
    g: Infinity,
    h: Infinity,
    isStartNode: row === START_NODE_ROW && col === START_NODE_COL,
    isEndNode: row === END_NODE_ROW && col === END_NODE_COL,
    isVisited: false,
    isObstacle: false,
    neighborNodes: [],
    parentNode: null,
  };

  return newNode;
};

const getNewGridWithToggledObstacle = (grid, row, col) => {
  const newGrid = grid.slice();
  const node = newGrid[row][col];
  const newNode = {
    ...node,
    isObstacle: !node.isObstacle,
  };
  newGrid[row][col] = newNode;
  return newGrid;
};
