// A* Algorithm
// Returns list of nodes that have been visited, and
// each of the nodes' parent node which will be used
// for creating our final shortest path

export function astar(startNode, endNode) {
  // Inital check on our parameters
  if (!startNode || !endNode || startNode === endNode) {
    return false;
  }

  // Create visited array for visualization
  const visitedNodes = [];

  // Start by adding our start Node
  // Calculate initial g,h, and f
  startNode.g = 0;
  startNode.h = getEuclideanDistance(startNode, endNode);
  startNode.f = startNode.g + startNode.h;

  // Push in startNode as first in our list
  const nodeList = [];
  nodeList.push(startNode);

  // A* algorithm starts here....
  while (nodeList.length > 0) {
    // Normally we would use a priority queue here to omptimize the
    // algorithm instead of sorting our list everytime. Javascript doesn't
    // have anything built in so for simplicity will just use this method for now.
    // A priority queue would do this for us as we add in our nodes with a min heap.

    // Sort from greatest to least based on the heuristic function. Sorting backwards,
    // so we can just use the pop() function on our list to remove the next queued node easily.
    nodeList.sort((a, b) => (a.f < b.f ? 1 : -1));

    // Check to see if the next node in the list is visited or the end node.
    // If so remove it, we do not need to check it
    while (
      nodeList.length > 0 &&
      (nodeList[nodeList.length - 1].isVisited ||
        nodeList[nodeList.length - 1].isObstacle ||
        nodeList[nodeList.length - 1] === endNode)
    ) {
      // Check to see if we are finished/at the end node
      if (nodeList[nodeList.length - 1] === endNode) {
        return visitedNodes;
      }
      nodeList.pop();
    }

    // Make sure our list is not empty
    if (nodeList.length > 0) {
      // Mark current node as visited, add to visited array
      const currentNode = nodeList[nodeList.length - 1];
      currentNode.isVisited = true;
      visitedNodes.push(currentNode);

      for (var i = 0; i < currentNode.neighborNodes.length; i++) {
        // Get current neighbor being scanned
        var nodeNeighbor = currentNode.neighborNodes[i];

        // If the node neighbor has not been visited and is not an
        // obstacle add it to the node list
        if (!nodeNeighbor.isVisited && !nodeNeighbor.isObstacle)
          nodeList.push(nodeNeighbor);

        // Calculate possible lower g value
        var possibleLowerG =
          currentNode.g + getEuclideanDistance(currentNode, nodeNeighbor);

        // If lower, update the current g and the parent.
        // Recalculate the new h
        if (possibleLowerG < nodeNeighbor.g) {
          nodeNeighbor.parentNode = currentNode;
          nodeNeighbor.g = possibleLowerG;
          nodeNeighbor.h = getEuclideanDistance(nodeNeighbor, endNode);

          // f(n) = g(n) + h(n)
          nodeNeighbor.f = nodeNeighbor.g + nodeNeighbor.h;
        }
      }
    } else {
      break;
    }
  }
}

// Euclidean hueristic function
const getEuclideanDistance = (nodeA, nodeB) => {
  return Math.sqrt(
    (nodeA.col - nodeB.col) * (nodeA.col - nodeB.col) +
      (nodeA.row - nodeB.row) * (nodeA.row - nodeB.row)
  );
};
