import React, { Component } from "react";

import Node from "./../Node/Node";

export default class Display extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nodes: [],
    };
  }

  componentDidMount() {
    this.setState({ nodes: nodes });
  }

  render() {
    <button onClick={() => this.astar()} Run astar></button>;
    return (
      <div className="grid">
        {nodes.map((row, rowIdx) => {
          return (
            <div key={rowIdx}>
              {row.map((node, nodeIdx) => {
                const { isStartNode, isEndNode } = node;
                return (
                  <Node
                    key={nodeIdx}
                    isStartNode={isStartNode}
                    isEndNode={isEndNode}
                  ></Node>
                );
              })}
            </div>
          );
        })}
      </div>
    );
  }
}

const createGrid = () => {
  // Display Grid and Nodes
  const grid = [];
  for (let row = 0; row < 20; row++) {
    const currentRow = [];
    for (let col = 0; col < 20; col++) {
      currentRow.push(currentNode);
    }
    nodes.push(currentRow);
  }
};

const createNewNode = (col, row) => {
  const newNode = {
    col: Number,
    row: Number,
    g: Number,
    h: Number,
    isStart: Boolean,
    isEnd: Boolean,
    isVisited: Boolean,
    isObstacle: Boolean,
    neighbors: Array,
    parentNode: Object,

    col,
    row,
    g: Infinity,
    h: Infinity,
    isStartNode: row === 0 && col === 0,
    isEndNode: row === 19 && col === 19,
    isVisited: false,
    isObstacle: false,
    neighbors: null,
    parentNode: null,
  };
};
